def ttr(text):
    tokenized_text = text.split(" ")
    count_words = 0
    counted_words = []

    for each_word in tokenized_text:

      if each_word not in counted_words:
        counted_words.append(each_word)
        count_words+=1

    return count_words/len(tokenized_text)

if __name__ == '__main__':

    print("Type token ratio is:", ttr("Wenn Fliegen hinter Fliegen fliegen, fliegen Fliegen Fliegen nach."))   #0.666
    print("Type token ratio is:", ttr(lorem_ipsum))  #0.132
